package Rest.client;import org.apache.http.client.ClientProtocolException;import org.apache.http.client.methods.HttpGet;import org.apache.http.impl.client.DefaultHttpClient;import java.io.IOException;import static Rest.client.Response.httpResponse;public class HttpRestGet {    public void get(){        try {            DefaultHttpClient httpClient = new DefaultHttpClient();            HttpGet getRequest = new HttpGet(                    "http://localhost:8080/rest/employees");            getRequest.addHeader("accept", "application/json");            httpResponse ( httpClient, httpClient.execute(getRequest), getRequest );        } catch (IOException e) {            e.printStackTrace();        }    }}