package Rest.client;import org.apache.http.HttpResponse;import org.apache.http.client.methods.HttpPost;import org.apache.http.entity.StringEntity;import org.apache.http.impl.client.DefaultHttpClient;import java.io.IOException;import static Rest.client.OutputServer.run;class HttpRestPost {    void post() {        try {            DefaultHttpClient httpClient = new DefaultHttpClient ();            HttpPost postRequest = new HttpPost (                    "http://localhost:8080/rest/employees" );            StringEntity input = new StringEntity ( "{\"qty\":100,\"name\":\"iPad 4\"}" );            input.setContentType ( "application/json" );            postRequest.setEntity ( input );            HttpResponse response = httpClient.execute ( postRequest );            if (response.getStatusLine ().getStatusCode () != 201) {                throw new RuntimeException ( "Failed : HTTP error code : "                        + response.getStatusLine ().getStatusCode () );            }            run ( httpClient, response );        } catch (IOException e) {            e.printStackTrace ();        }    }}