package Rest.client;import org.apache.http.client.methods.HttpPost;import org.apache.http.entity.StringEntity;import org.apache.http.impl.client.DefaultHttpClient;import java.io.IOException;import static Rest.client.Response.httpResponse;class HttpRestPost {    void post() {        try {            DefaultHttpClient httpClient = new DefaultHttpClient ();            HttpPost postRequest = new HttpPost (                    "http://localhost:8080/rest/employees" );            StringEntity input = new StringEntity ( "{\"empId\":\"E02\",\"empName\":\"Samat\",\"position\":\"---\",\"salary\":\"231231\",\"city\":\"Kazan\"}" );            input.setContentType ( "application/json" );            postRequest.setEntity ( input );            httpResponse ( httpClient, httpClient.execute( postRequest ), postRequest );        } catch (IOException e) {            e.printStackTrace ();        }    }}