package Rest.client;import java.util.Scanner;public class Main {    public static void main(String[] args) {        HttpRestGet restGet = new HttpRestGet ();        HttpRestPost httpRestPost = new HttpRestPost ();        Scanner scanner = new Scanner ( System.in );        if (scanner.hasNext ( "GET" )) {            restGet.get ();        } else if (scanner.hasNext ( "POST" )) {            httpRestPost.post ();        }    }}