package Rest.client;import org.apache.http.HttpResponse;import org.apache.http.impl.client.DefaultHttpClient;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class OutputServer {    static void run(DefaultHttpClient httpClient, HttpResponse response) throws IOException {        BufferedReader br = new BufferedReader (                new InputStreamReader ( ( response.getEntity ().getContent () ) ) );        String output;        System.out.println ( "Output from Server .... \n" );        while (( output = br.readLine () ) != null) {            System.out.println ( output + "\n" );        }        httpClient.getConnectionManager ().shutdown ();    }}