package dao;import model.Employee;import java.util.*;public class EmployeeDAO {    private static final Map<String, Employee> empMap = new HashMap<String, Employee> ();    static {        initEmps();    }    private static void initEmps() {        Employee emp1 = new Employee("E01", "Smith", "Clerk","231231","Kazan");        empMap.put(emp1.getEmpId (), emp1);    }    public static Employee getEmployee(String empId) {        return empMap.get ( empId );    }    public static Employee addEmployee(Employee emp) {        empMap.put ( emp.getEmpId (), emp );        return emp;    }    public static Employee updateEmployee(Employee emp) {        empMap.put ( emp.getEmpId (), emp );        return emp;    }    public static void deleteEmployee(String empId) {        empMap.remove ( empId );    }    public static List<Employee> getAllEmployees() {        Collection<Employee> c = empMap.values ();        List<Employee> list = new ArrayList<> ();        list.addAll ( c );        return list;    }    List<Employee> list;}