package model;import javax.xml.bind.annotation.XmlAccessType;import javax.xml.bind.annotation.XmlAccessorType;import javax.xml.bind.annotation.XmlRootElement; @XmlRootElement(name = "employee")@XmlAccessorType(XmlAccessType.FIELD)public class Employee {     private String empId;    private String empName;    private String position;    private String salary;    private  String city;    public Employee() {     }     public Employee(String empId, String empName, String position,String salary,String city) {        this.empId = empId;        this.empName = empName;        this.position = position;        this.salary=salary;        this.city=city;    }     public String getEmpId() {        return empId;    }     public void setEmpId(String empId) {        this.empId = empId;    }     public String getEmpName() {        return empName;    }     public void setEmpName(String empName) {        this.empName = empName;    }     public String getPosition() {        return position;    }     public void setPosition(String position) {        this.position = position;    }    public String getSalary() {        return salary;    }    public void setSalary(String salary) {        this.salary = salary;    }    public String getCity() {        return city;    }    public void setCity(String city) {        this.city = city;    }}