package socket;import java.io.*;import java.net.Socket;public class SocketServer {    public static void setSocket() throws IOException {        String hostName = "localhost";        int port = 8080;        Socket socket = new Socket ( hostName, port );        OutputStream output = socket.getOutputStream ();        String out = "samat";        PrintWriter printWriter = new PrintWriter ( output,  true  );        printWriter.write ( out );        InputStream inputStream = socket.getInputStream ();        BufferedReader reader = new BufferedReader ( new InputStreamReader ( inputStream ) );        String line = reader.readLine ();        System.out.println ( line );        socket.close ();    }}